"main";let Cars;_8a0‍.w('../models/Cars',[["default",function(v){Cars=v}]]);let Yup;_8a0‍.w('yup',[["*",function(v){Yup=v}]]);



class CreateCarsService {
  async execute(req, res) {
    const schema = Yup.object().shape({
      brand: Yup.string().required(),
      model: Yup.string().required(),
      year: Yup.number().required(),
      gas_type: Yup.string().required(),
      color: Yup.string().required(),
      price: Yup.number().required(),
    });

    if(!(schema.isValid(req.body))) {
      return res.status(400).json({ error: "Validation error"});
    }

    try{

    const createCars = await Cars.create(req.body);

    return res.status(200).json(createCars);
  
    } catch(err) {
      return res.status(400).json(err.message);
    }
  }
}