"main";let Yup;_a43‍.w('yup',[["*",function(v){Yup=v}]]);let Cars;_a43‍.w('../models/Cars',[["default",function(v){Cars=v}]]);



class UpdateCarsService {
  async execute(req, res) {
    const schema = Yup.object().shape({
      brand: Yup.string().required(),
      model: Yup.string().required(),
      year: Yup.number().required(),
      gas_type: Yup.string().required(),
      color: Yup.string().required(),
      price: Yup.number().required(),
    });

    if(!(schema.isValid(req.body))){
      return res.status(400).json({ error: 'Validation error' });
    }

    try {
      const car = await Cars.findByPk(req.params.id);
      console.log(car);

      const { 
        id, 
        brand, 
        model, 
        year, 
        gas_type, 
        color, 
        price
      } = await Cars.update(req.body);

      return res.status(200).json({
        id, 
        brand, 
        model, 
        year, 
        gas_type, 
        color, 
        price 
      });
    } catch (err) {
      return res.status(400).json(err.message);
    }
  }
}

_a43‍.d(new UpdateCarsService());